Northwind.Common.Components.Grid.PageView=Ember.View.extend({classNames:["pull-left","table-page"],first:0,last:0,defaultTemplate:Ember.Handlebars.compile("Showing {{view.first}} - {{view.last}} from {{controller.totalCount}}"),didPageChange:function(){console.log("PageView.didPageChange");var a=this.get("controller.limit"),b=this.get("controller.totalCount"),c=this.get("controller.offset");this.set("first",c),this.set("last",Math.min(b,c+a))}.observes("controller.page","controller.totalCount")})