Northwind.ObjectController=Ember.ObjectController.extend({isEditing:!1,actions:{edit:function(){this.set("isEditing",!0)},acceptChanges:function(){this.set("isEditing",!1),this.get("model.isSaveable")?this.send("save"):this.send("remove")},save:function(){this.get("model").save()},remove:function(){this.get("model").deleteRecord(),this.send("save")}}})